exports.CompanyProfileSave=function(client) {
	client
	  .useXpath()
	  .click('/html/body/div[1]/div[2]/div/div/div/div[1]/div/div[3]/div/button[2]')
	  .pause(2000)
	  .assert.containsText('/html/body/div[1]/div[2]/div/div/div/div[3]/div[1]/div/span', 'Company Profile Has Been Saved')
	  .pause(2000)
};

exports.logout=function(client) {
	client
	  .useXpath()
	  .click('//*[@id="navbar"]/ul[2]/li[2]/a')
	  .pause(2000)
	  .click('//*[@id="navbar"]/ul[2]/li[2]/ul/li[4]/a')
	  .pause(2000)
};

exports.end=function(client) {
	client
	  .end(); 
};







