def restEntityVolumeArr = holder2.getNodeValues( '//ns:results/ns:e/ns:content_volume'  )
for( int z=0; z<restEntityTypeArr.length; z++) {  
	restMap.put(restEntityTypeArr[z],restEntityVolumeArr[z]);
}
