	def fail = item.fail();
	if(fail == -1) {
		log.info((++failCount) + ": " + item.jdbcKey + "-" + item.jdbcValue + "-" + item.restValue);
	} else if(fail > 0) {
		log.info((++failCount) + ": " + item.jdbcKey + "-" + item.jdbcValue + "-" + item.restValue + " --- PERCENT: " + fail);
	} else {
//		log.info(item.jdbcKey + "-" + item.jdbcValue + "-" + item.restValue);
	}
}

